        for(set(i,0),i LT hotspot.count,inc(i),
            if(hotspot[get(i)].hotspot_ctrl,
                set(hotspot[get(i)].visible, false);
            );
        );



calc(local.xframes, (caller.imagewidth /framewidth) BOR 0);
calc(local.frames, xframes * ((caller.imageheight / frameheight) BOR 0));
def(local.frame, integer, 0);


calc(caller.crop, '0|0|' + framewidth + '|' + frameheight);


setinterval(calc('crop_anim_' + caller.name), calc(1.0 / framerate),
	if(caller.loaded,
		inc(frame);
		if(frame GE frames, if(caller.onlastframe !== null, callwith(caller, onlastframe() ) ); set(frame,0); );
		mod(xpos, frame, xframes);
		div(ypos, frame, xframes);
		Math.floor(ypos);
		mul(xpos, framewidth);
		mul(ypos, frameheight);
		calc(caller.crop, xpos + '|' + ypos + '|' + framewidth + '|' + frameheight);
	  , 

		clearinterval(calc('crop_anim_' + caller.name));
	);
);

js(startNormalView());
set(autorotate.enabled, false);
copy(lp_scene, xml.scene);
copy(lp_hlookat, view.hlookat2||view.hlookat);
copy(lp_vlookat, view.vlookat2||view.vlookat);
copy(lp_fov, view.fov2||view.fov);
copy(lp_fovmax, view.fovmax);
copy(lp_limitview, view.limitview);
set(view.limitview, lookto);
lookat(lp_hlookat, 90, 150, 1, 0, 0);
set(events[lp_events].onloadcomplete,
	delayedcall(0,
		if(lp_scene === xml.scene,
		set(control.usercontrol, off);
		copy(view.limitview, lp_limitview);
		set(view.vlookatmin, null);
		set(view.vlookatmax, null);

		tween(view.hlookat|view.vlookat|view.fov|view.distortion, calc('' + lp_hlookat + '|' + lp_vlookat + '|' + lp_fov + '|' + 0.0),
			3.0, easeInOutQuad,
			set(control.usercontrol, all);
			tween(view.fovmax, get(lp_fovmax));



			js(endNormalView());
			);
		);
	);
);

calc(deg2rad, Math.PI / 180.0);


calc(hfov, 0.5 * (%1) * deg2rad);
Math.tan(hfov);
set(width, calc(hfov * 1000));
set(height, 'prop');


calc(yaw, -(%2) * deg2rad);
calc(pitch, (%3) * deg2rad);
calc(roll, -(%4) * deg2rad);
Math.cos(ch, yaw);
Math.sin(sh, yaw);
Math.cos(ca, pitch);
Math.sin(sa, pitch);
Math.cos(cb, roll);
Math.sin(sb, roll);
Math.atan2(yaw,  calc(cb*sh - sb*sa*ch), calc(ca*ch));
Math.atan2(roll, calc(sb*ch - cb*sa*sh), calc(cb*ca));
Math.asin(pitch, calc(cb*sa*ch + sb*sh));
calc(ath, yaw / deg2rad);
calc(atv, pitch / deg2rad);
calc(rotate, roll / deg2rad);

